export default {
  name: "communityPage",
  mounted: function mounted() {
    var _this = this;

    var LocationSearch = new window.google.maps.places.Autocomplete(document.getElementById("autoComplete"), {
      bounds: window.google.maps.LatLngBounds(new window.google.maps.LatLng(-25.274398, 133.775136))
    });
    LocationSearch.setComponentRestrictions({
      country: ["au"]
    });
    LocationSearch.addListener("place_changed", function () {
      var googleLocation = LocationSearch.getPlace(); //console.log(googleLocation)

      _this.googleLocation = {
        'fullAddress': googleLocation.formatted_address,
        'shortName': googleLocation.name,
        "viewport": googleLocation.geometry.viewport,
        "id": googleLocation.place_id
      };
    });
  },
  data: function data() {
    return {
      inputText: "",
      googleLocation: ""
    };
  }
};
